package ir.at.todolist.diimport android.app.Applicationimport androidx.room.Roomimport dagger.Moduleimport dagger.Providesimport dagger.hilt.InstallInimport dagger.hilt.components.SingletonComponentimport ir.at.todolist.data.database.DaoAppimport ir.at.todolist.data.database.DbAppimport javax.inject.Singleton@Module@InstallIn(SingletonComponent::class)object AppModules {    @Singleton    @Provides    fun getDbApp(application: Application) : DbApp =        Room.databaseBuilder(application , DbApp::class.java , "My_Db").build()    @Singleton    @Provides    fun getDao(dbApp: DbApp) : DaoApp = dbApp.getDaoApp()}