package ir.at.todolist.feature.addTaskimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport dagger.hilt.android.lifecycle.HiltViewModelimport ir.at.todolist.data.repo.Repositoryimport ir.at.todolist.data.Taskimport kotlinx.coroutines.Dispatchersimport kotlinx.coroutines.launchimport javax.inject.Inject@HiltViewModelclass AddTaskViewModel @Inject constructor(private val repository: Repository) : ViewModel() {    fun addTask(task: Task){        viewModelScope.launch(Dispatchers.IO){            repository.addTask(task)        }    }}